import React, { useState } from 'react';
import { NavLink } from 'react-router-dom';
import { useSelector } from 'react-redux';
import ProfileButton from './ProfileButton';
import './Navigation.css';
import { useDispatch } from "react-redux";
import * as SearchAction from '../../store/search'
import Landing from '../Landing';
import { useHistory } from 'react-router-dom/cjs/react-router-dom.min';

function Navigation({ isLoaded }){
	const dispatch = useDispatch()
	const sessionUser = useSelector(state => state.session.user);
	const [search,setSearch]= useState("")
	const history = useHistory()


	const handleSeach = async (e) =>{
		e.preventDefault()
		await dispatch(SearchAction.getSearchThunk(search))


	}



	return (
		<>
		<div className='nav-container'>
				<div className='home-link-container'>
					<NavLink className='home-link' exact to="/">Fetsy</NavLink>
				</div>


				<form  onSubmit={handleSeach} className='search-label'>
					<label >
						<input 
						type='text'
						value={search}
						onChange={(e)=>setSearch(e.target.value)}
						className='search-input'
						placeholder='Search for anything'
						/>
						<i class="fa-solid fa-magnifying-glass" style={{color: "#000000"}}></i>
					</label>
					</form>



		<div className='profile-icon'>
			{isLoaded && (

					<ProfileButton user={sessionUser} />


			)}
			</div>
			<div><i class="fa-sharp fa-solid fa-cart-shopping" style={{color:"rgb(162, 162, 162)",fontSize:"20px"}}></i> </div>
			<div><i class="fa-regular fa-heart"style={{color:"rgb(162, 162, 162)",fontSize:"20px"}}></i></div>
			<div><i class="fa-regular fa-bell"style={{color:"rgb(162, 162, 162)",fontSize:"20px"}}></i></div>
		</div>

		<div className="landing-container">
  {search[0] ? (
    search.map((element, index) => (
      <div
        key={index}
        className="product"
        id="products"
        onClick={() => {
          history.push(`/products/${element.id}`);
        }}
      >
        <img
          src={element.image1}
          alt={`Product ${index}`}
          style={{ width: '200px', height: '200px' }}
        />
        <div className="price">
          <i
            className="fa-solid fa-dollar-sign"
            style={{ color: '#000000', marginRight: '5px' }}
          ></i>
          {element.price}
        </div>
      </div>
    ))
  ) : (
    <div>
      <Landing />
    </div>
  )}
</div>






	</>

	);
}

export default Navigation;
