import React, { useEffect, useState } from "react";
import { useDispatch } from "react-redux";
import * as ReviewActions from '../../store/review'
import { useSelector } from 'react-redux';
import * as ProductActions from '../../store/products'
import './details.css'
import { useParams } from "react-router-dom/cjs/react-router-dom.min";





const ProductDetails = () =>{

    // const reviews = useSelector(state => state.review.singleProduct)
    const dispatch = useDispatch()
    const {product_id} = useParams()
    const product_id_num = Number(product_id)
    console.log("the product id is", product_id_num)

    useEffect(()=>{
        async function fetchData (){
           const product =  await ProductActions.getProductByIdThunk(product_id_num)
            console.log("the product is", product)
        }
        fetchData()
    },[dispatch,product_id_num])
    return (
        <>
        <h1>hello from product details</h1>



        </>
    )



}

export default ProductDetails
