import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { useHistory } from "react-router-dom";
import * as UserAction from '../../store/session';
import NewProduct from '../createProduct'

function UserProfile() {
  const dispatch = useDispatch();
  const history = useHistory();
  const sessionUser = useSelector(state => state.session.user);
  const [products, setProducts] = useState([]);

  useEffect(() => {
    async function fetchData() {
      const data = await dispatch(UserAction.getUserProductThunk(sessionUser.id));
      setProducts(data);
    }
    fetchData();
  }, [dispatch, sessionUser.id]);

  const handleCreateProduct = async () => {
    history.push('/new_product');
  };

  const updateProductsState = (newProduct) => {
    setProducts((prevProducts) => [...prevProducts, newProduct]);
  };

  return (
    <div>
      <div className="header">
        <img src={sessionUser.image} alt="User Avatar" style={{ height: "60px", width: "60px", borderRadius: "15px" }} />
        {sessionUser.username}
        <button className="create-product" onClick={handleCreateProduct}>Create Product</button>
      </div>

      <div className="products-containers">
        <div className="product-items">
          {products.map((product, index) => (
            <div key={index} className={`product${index}`}>
              <img src={product.image} alt={`Product ${index}`} style={{ height: "200px", width: "200px" }} />
            </div>
          ))}
        </div>
      </div>

      {/* Pass the callback function to NewProduct */}
      <NewProduct onProductCreated={updateProductsState} />
    </div>
  );
}

export default UserProfile;
