const GET_PRODUCTS = 'get/products'




//ACTIONS
export const get_products = (data) =>{
    return {
        type:GET_PRODUCTS,
        payload:data
    }
}








// THUNKS
export const getAllProductThunk = () => async (dispatch, getState) => {


        const res = await fetch('/api/products/',{
            method: "GET"
        });


        if (res.ok) {
            const data = await res.json();

        console.log("the data returning is ",data)
            dispatch(get_products(data));
            return data;
        } else {

            const errorData = await res.json();
            throw new Error(errorData.error || 'Failed to fetch data');
        }

}


//REDUCDER
const inital_state = {products:{}}

const productReducer = (state=inital_state, action)=>{

    switch(action.type) {
        case GET_PRODUCTS:
            const newState = {...state, products:{...state.products}}
            //make a copy of all the keys in the data not whats in them
            newState.products.allProducts = action.payload.data
            return newState
        default:
            return state

    }
}


export default productReducer
