import React, { useState, useEffect, useRef } from "react";
import * as UserAction from '../../store/session'
import { useDispatch,useSelector  } from "react-redux";
import './userProfile.css'
import { useHistory } from "react-router-dom/";
import NewProduct from "../createProduct";

function UserProfile({prop}){

const dispatch = useDispatch()
const sessionUser = useSelector(state=>state.session.user)
const [products,setProducts] = useState()
const history = useHistory()


    useEffect(()=>{
        async function FetchData (){
            const data = await dispatch(UserAction.getUserProductThunk(sessionUser.id))

            setProducts(data)
        }
        FetchData()

    },[dispatch,sessionUser.id])

    const updateProductsState = (newProduct) => {
        setProducts((prevProducts) => [...prevProducts, newProduct]);
      };


    async function handleProduct(e){
        e.preventDefault()


        return <NewProduct onProductCreated={updateProductsState} />
    }

    if(!products){
        return null;
    }
    const values = Object.values(products)

    return (


        <>
        <div>
            <div>
                <div className="header">
                <img src={sessionUser.image} style={{height:"60px",width:"60px", borderRadius:"15px"}} />
                {sessionUser.username}
                <button className="create-product" onClick={handleProduct}>Create Product</button>
                </div>




            </div>
        </div>
        <div className="products-containers">
        <div className="product-items">
            {values.map((element, index) => (

                <div key={index} className={`product${index}`}> 
                    <img src={element.image} style={{height:"200px",width:"200px"}}/>
                </div>

            ))}
            </div>

        </div>



        <div className="extra-stuff">

        </div>
        </>
    )
}

export default UserProfile
